<script>
import { computed, reactive, toRef,toRefs } from 'vue'
import Sidebar from '../../components/Loyout/Sidebar/Sidebar.vue'
import { useStore } from 'vuex'
import { isNaLL, validateEMail } from '../../utils/utils'
import { shoElmessage } from '../../utils/Elmessage'
import { Changeemail ,Resetpwd} from '../../api/Userinfo'
import { Findersend } from '../../api/Revision'
export default {
  components: { Sidebar },
  data(){
      return{
          form:{
              username:"",
              email:"",
              password:"",
              code:""
          },
          time:"获取验证码",
          rules: {
                // username:[{required: false, message: '请输入用户名', trigger: 'blur'},{ min: 3, max: 10, message: '长度3个字符以上 10 个字符以下', trigger: 'blur' }],
                // password: [{ required: true, validator: validatePsdReg, trigger: 'blur'}],
                email: [{required: true,validator: validateEMail, trigger: 'blur' }],
                // mobile: [{required: true,validator: validatePhone, trigger: 'blur' }],
                code: [{required: true,message: '请输入验证码', trigger: 'blur' },{ min: 4, max: 6, message: '必须6位数', trigger: 'blur' }],
            }
      }
  },
  setup(){
      const store = useStore();
      const userinfo = computed(() => store.getters['userinfo/userinfo'])
      
      return{
          userinfo,
      }
  },
  methods:{
      timer(){
          if(!isNaLL(this.form.email)){
              return shoElmessage(true,'请输入没有注册过的邮箱','error')
          }
          if(this.time === '获取验证码'){
            Findersend({
                email:this.form.email,
                event:""
            }).then((res)=>{
                if(res.code == 1){
                    shoElmessage(true,'发送成功')
                    this.time = 60
                    let settimer = setInterval(() =>{
                        this.time--
                        if(this.time == 0){
                            this.time = ' 获取验证码'
                            clearInterval(settimer);
                        }
                    },1000)
                }else{
                    shoElmessage(true,`发送失败，原因：${res.msg}`,'error')
                }
            })
             
          }
      },
      Changeemail(){
          
      },
      Resetpwd(){
          
      },
      onSubmit(formname){
        this.$refs[formname].validate((valid) => {
            if(valid){
                Changeemail({
                    email:this.form.email,
                    captcha:this.form.code
                }).then(res =>{
                    if(res.code ==1){
                        shoElmessage(true,'修改成功')
                    }else{
                        shoElmessage(true,`修改失败，原因：${res.msg}`,'error')
                    }
                })
            }
        })
      }

  }

}
</script>
